document.addEventListener('DOMContentLoaded',nav)
function nav(){const body=document.querySelector('body');const burger=document.querySelector('.burger');const nav=document.querySelector('.navigation');burger.addEventListener('click',()=>{nav.classList.toggle('show');body.classList.toggle('dontmove')})}
document.addEventListener('DOMContentLoaded',function(){var splide=new Splide('.glider',{type:'loop',autoplay:'play',perPage:2,perMove:1,resetProgress:!1,gap:'25px',pagination:!1,arrows:!1,drag:!1,breakpoints:{600:{perPage:1,},}});splide.mount()})